    filter(essround == 11) |&gt; 
    group_by(Gender = as_factor(gndr)) |&gt;
    summarise(ESS = weighted.mean(gincdif_inv, w = pspwght, na.rm = T))  |&gt; 
    left_join(
        df_vs |&gt; 
        filter(study == "EES24") |&gt; 
        group_by(Gender = as_factor(gndr)) |&gt; 
        summarise(VS = weighted.mean(redist_inv, w = weight, na.rm = T))
    ) |&gt; 
    gt()  |&gt; 
    fmt_number(everything(), decimals = 1)




